import React, {Component} from 'react'
import DisplayList from './DisplayList'
import { Navbar, Nav, NavDropdown, Form, FormControl, Button } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'

import Cart from './Cart'


export default class FilteredList extends Component {

    constructor(props) {
        super(props)
        this.state = {
            exotic: "All",
            type: "All",
            price: 0,
            cartPrice: 0,
            quantity: 0,
            cartList: []
        }
    }

    onSelectFilterType = event => {
        this.setState({
            type: event
        })
    }

    matchesFilterType = item => {
        if(this.state.type === "All") {
            return true
        } else if (this.state.type === item.type) {
            return true 
        } else {
            return false 
        }
    }

    onSelectFilterExotic = event => {
        this.setState({
            exotic: event
        })
    }

    matchesFilterExotic = item => {
        if(this.state.exotic === "All") {
            return true
        } else if (this.state.exotic === item.exotic) {
            return true 
        } else {
            return false 
        }
    }

    filterBoth = item => {
        return this.matchesFilterType(item) && this.matchesFilterExotic(item)
    }

    onSelectSortPrice = event => {
        this.setState({
            price: event
        })
    }


    addItem(key, exotic, type, price, cartPrice, quantity, cartList) {

        if(this.state.cartList[key] == null) {
            this.state.cartList[key] = {}
        }




        this.setState({
            cartList
        })
    }

    removeItem = event => {
        event.preventDefault()


        this.setState({
            cartList
        })
    }



    render () {

        return (

            <div>


                <Navbar collapseOnSelect expand="lg" bg="red" variant="light">
            <Navbar.Brand href="#home">Pet-Shop</Navbar.Brand>
            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
              <Nav className="mr-auto">


                <NavDropdown title="Pet Type" id="collasible-nav-dropdown">

                  <NavDropdown.Item>
                      <Nav.Link eventKey="Mammal" onSelect={this.onSelectFilterType}>Mammal</Nav.Link>
                      </NavDropdown.Item>

                  <NavDropdown.Item>
                      <Nav.Link eventKey="Bird" onSelect={this.onSelectFilterType}>Bird</Nav.Link>
                      </NavDropdown.Item>

                  <NavDropdown.Item>
                      <Nav.Link eventKey="Fish" onSelect={this.onSelectFilterType}>Fish</Nav.Link>
                      </NavDropdown.Item>

                  <NavDropdown.Divider />

                  <NavDropdown.Item>
                      <Nav.Link eventKey="All" onSelect={this.onSelectFilterType}>All</Nav.Link>
                      </NavDropdown.Item>

                </NavDropdown>

                <NavDropdown title="Exotic Level" id="collasible-nav-dropdown">

                  <NavDropdown.Item>
                    <Nav.Link eventKey="Low" onSelect={this.onSelectFilterExotic}>Low</Nav.Link>
                    </NavDropdown.Item>

                  <NavDropdown.Item>
                    <Nav.Link eventKey="Medium" onSelect={this.onSelectFilterExotic}>Medium</Nav.Link>
                    </NavDropdown.Item>

                  <NavDropdown.Item>
                    <Nav.Link eventKey="High" onSelect={this.onSelectFilterExotic}>High</Nav.Link>
                    </NavDropdown.Item>

                  <NavDropdown.Divider />

                  <NavDropdown.Item>
                    <Nav.Link eventKey="All" onSelect={this.onSelectFilterExotic}>All</Nav.Link>
                    </NavDropdown.Item>

                </NavDropdown>

                <NavDropdown title="Price" id="collasible-nav-dropdown">
                  <NavDropdown.Item href="#action/3.1">Low to High</NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.2">High to Low</NavDropdown.Item>
                  <NavDropdown.Divider />
                  <NavDropdown.Item href="#action/3.4">Our Picks</NavDropdown.Item>
                </NavDropdown>


              </Nav>
            </Navbar.Collapse>
          </Navbar>




                <div class="maingrid">


                <DisplayList list={this.props.list.filter(this.filterBoth)} addItem={this.addItem} />

                <Cart list={this.props.list} removeItem={this.removeItem}/>


                </div>




            </div>



        )


    }


}
