import React, {Component} from 'react'
import DisplayList from './DisplayList'
import { Navbar, Nav, NavDropdown, Form, FormControl, Button } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'

import Cart from './Cart'


export default class FilteredList extends Component {

    constructor(props) {
        super(props)
        this.state = {
            exotic: "All",

            type: "All",

            sort: "All",

            cartPrice: 0,

            cartList: {},

            currentItem: {
                name: '',
                img: '',
                type: '',
                exotic: '',
                price: '',
                quantity: ''
            }
        }
    }

    onSelectFilterType = event => {
        this.setState({
            type: event
        })
    }

    matchesFilterType = item => {
        if(this.state.type === "All") {
            return true
        } else if (this.state.type === item.type) {
            return true 
        } else {
            return false 
        }
    }

    onSelectFilterExotic = event => {
        this.setState({
            exotic: event
        })
    }

    matchesFilterExotic = item => {
        if(this.state.exotic === "All") {
            return true
        } else if (this.state.exotic === item.exotic) {
            return true 
        } else {
            return false 
        }
    }

    filterBoth = item => {
        return this.matchesFilterType(item) && this.matchesFilterExotic(item)
    }

    onSelectSortPrice = event => {
        this.setState({
            sort: event
        })
    }


    addItem(name, img, type, exotic, price, quantity) {


        if(this.state.cartList[name] == null) {
            this.state.currentItem = {name:name, img:img, type:type, exotic:exotic, price:price, quantity:quantity}
            const cartList = [...this.state.cartList, this.state.currentItem]
        } else{
            this.state.cartList[name].quantity +=1
        }

        this.setState({
            cartList:cart
        })
    }


    removeItem(name) {

        const cartList = this.state.cartList
        if(this.state.cartList[name].quantity == 1) {
            this.state.cartList[name] = null
        } else {
            this.state.cartList[name].quantity -=1 
        }

        this.setState({
            cartList:cartList
        })
    }



    render () {

        return (

            <div>


                <Navbar collapseOnSelect expand="lg" bg="red" variant="light">
            <Navbar.Brand href="#home">Pet-Shop</Navbar.Brand>
            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
              <Nav className="mr-auto">


                <NavDropdown title="Pet Type" id="collasible-nav-dropdown">

                  <NavDropdown.Item>
                      <Nav.Link eventKey="Mammal" onSelect={this.onSelectFilterType}>Mammal</Nav.Link>
                      </NavDropdown.Item>

                  <NavDropdown.Item>
                      <Nav.Link eventKey="Bird" onSelect={this.onSelectFilterType}>Bird</Nav.Link>
                      </NavDropdown.Item>

                  <NavDropdown.Item>
                      <Nav.Link eventKey="Fish" onSelect={this.onSelectFilterType}>Fish</Nav.Link>
                      </NavDropdown.Item>

                  <NavDropdown.Divider />

                  <NavDropdown.Item>
                      <Nav.Link eventKey="All" onSelect={this.onSelectFilterType}>All</Nav.Link>
                      </NavDropdown.Item>

                </NavDropdown>

                <NavDropdown title="Exotic Level" id="collasible-nav-dropdown">

                  <NavDropdown.Item>
                    <Nav.Link eventKey="Low" onSelect={this.onSelectFilterExotic}>Low</Nav.Link>
                    </NavDropdown.Item>

                  <NavDropdown.Item>
                    <Nav.Link eventKey="Medium" onSelect={this.onSelectFilterExotic}>Medium</Nav.Link>
                    </NavDropdown.Item>

                  <NavDropdown.Item>
                    <Nav.Link eventKey="High" onSelect={this.onSelectFilterExotic}>High</Nav.Link>
                    </NavDropdown.Item>

                  <NavDropdown.Divider />

                  <NavDropdown.Item>
                    <Nav.Link eventKey="All" onSelect={this.onSelectFilterExotic}>All</Nav.Link>
                    </NavDropdown.Item>

                </NavDropdown>

                <NavDropdown title="Price" id="collasible-nav-dropdown">
                  <NavDropdown.Item>Low to High</NavDropdown.Item>


                  <NavDropdown.Item>High to Low</NavDropdown.Item>
                  <NavDropdown.Divider />

                  <NavDropdown.Item>
                    <Nav.Link eventKey="All" onSelect={this.onSelectFilterExotic}>Our Picks
                    </Nav.Link>
                  </NavDropdown.Item>
                </NavDropdown>


              </Nav>
            </Navbar.Collapse>
          </Navbar>




                <div class="maingrid">


                <DisplayList list={this.props.list.filter(this.filterBoth)} addItem={this.addItem} />

                <Cart cartList={this.state.cartList} removeItem={this.removeItem}/>


                </div>




            </div>



        )


    }


}
